    private void btConvertirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btConvertirActionPerformed
        double temp = Double.parseDouble(txtConvertir.getText());
        if (rdCent.isSelected())
            temp = convCentAFahr(temp);
        else
            temp = convFahrACent(temp);
        txtConvertir.setText(Double.toString(temp));
    }//GEN-LAST:event_btConvertirActionPerformed

