    private static double convCentAFahr(double arg0) {
        jaxws.SWConverTemps_Service service = new jaxws.SWConverTemps_Service();
        jaxws.SWConverTemps port = service.getSWConverTempsPort();
        return port.convCentAFahr(arg0);
    }

    private static double convFahrACent(double arg0) {
        jaxws.SWConverTemps_Service service = new jaxws.SWConverTemps_Service();
        jaxws.SWConverTemps port = service.getSWConverTempsPort();
        return port.convFahrACent(arg0);
    }
